Get-EventLog -LogName Security -InstanceId 4624 | Select-Object TimeGenerated, @{n='User';e={$_.ReplacementStrings[5]}}|where {$_.user -ne "SYSTEM" -and $_.user -ne "$(hostname)"+"$" -and ($_.user -notmatch "UMFD-*") -and $_.user -ne "NETWORK SERVICE" -and $_.user -ne "LOCAL SERVICE" -and $_.user -notmatch '^(DWM-\d+|[\dA-Fa-f]{8}-[\dA-Fa-f]{4}-[\dA-Fa-f]{4}-[\dA-Fa-f]{4}-[\dA-Fa-f]{12})$'}
pause